import React, { useEffect, useState } from 'react';
import '../styles/Bookings.css';
import axios from 'axios';

const Bookings = () => {
  const [bookings, setBookings] = useState([]);
  const userId = localStorage.getItem('userId');

  useEffect(() => {
    fetchBookings();
  }, []);

  const fetchBookings = async () => {
    const res = await axios.get('http://localhost:6001/fetch-bookings');
    setBookings(res.data.reverse());
  };

  const cancelTicket = async (id) => {
    await axios.put(`http://localhost:6001/cancel-ticket/${id}`);
    alert('Ticket cancelled!');
    fetchBookings();
  };

  return (
    <div className="all-flights-wrapper">
      <h1 className="all-flights-title">Your Bookings</h1>

      <div className="flights-grid">
        {bookings.filter(b => b.user === userId).map((booking) => (
          <div className="flight-card two-column-booking" key={booking._id}>
            <div className="booking-left">
              <p className="flight-info"><strong>Booking ID:</strong> {booking._id}</p>
              <p className="flight-info"><strong>Mobile:</strong> {booking.mobile}</p>
              <p className="flight-info"><strong>Email:</strong> {booking.email}</p>
              <p className="flight-info"><strong>Flight Name:</strong> {booking.flightName}</p>
              <p className="flight-info"><strong>Flight ID:</strong> {booking.flightId}</p>
              <p className="flight-info"><strong>Onboarding:</strong> {booking.departure}</p>
              <p className="flight-info"><strong>Destination:</strong> {booking.destination}</p>
            </div>

            <div className="booking-right">
              <div className="passenger-section">
                <p className="flight-info"><strong>Passengers:</strong></p>
                <ol className="passenger-list">
                  {booking.passengers.map((p, i) => (
                    <li key={i}>
                      <span><strong>Name:</strong> {p.name}, <strong>Age:</strong> {p.age}</span>
                    </li>
                  ))}
                </ol>
                {booking.bookingStatus === 'confirmed' && (
                  <p className="flight-info"><strong>Seats:</strong> {booking.seats}</p>
                )}
              </div>

              <p className="flight-info"><strong>Booking Date:</strong> {booking.bookingDate.slice(0, 10)}</p>
              <p className="flight-info"><strong>Journey Date:</strong> {booking.journeyDate.slice(0, 10)}</p>
              <p className="flight-info"><strong>Time:</strong> {booking.journeyTime}</p>
              <p className="flight-info"><strong>Total Price:</strong> â‚¹{booking.totalPrice}</p>

              <p className={`flight-info ${booking.bookingStatus === 'cancelled' ? 'cancelled' : 'confirmed'}`}>
                <strong>Status:</strong> {booking.bookingStatus}
              </p>

              {booking.bookingStatus === 'confirmed' && (
                <button className="btn btn-danger" onClick={() => cancelTicket(booking._id)}>
                  Cancel Ticket
                </button>
              )}
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Bookings;
