import React, { useContext, useEffect, useState } from 'react';
import '../styles/LandingPage.css';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import { GeneralContext } from '../context/GeneralContext';

const LandingPage = () => {
  const [error, setError] = useState('');
  const [checkBox, setCheckBox] = useState(false);
  const [departure, setDeparture] = useState('');
  const [destination, setDestination] = useState('');
  const [departureDate, setDepartureDate] = useState();
  const [returnDate, setReturnDate] = useState();
  const [Flights, setFlights] = useState([]);

  const { setTicketBookingDate } = useContext(GeneralContext);
  const navigate = useNavigate();
  const userId = localStorage.getItem('userId');

  useEffect(() => {
    const userType = localStorage.getItem('userType');
    if (userType === 'admin') {
      navigate('/admin');
    } else if (userType === 'flight-operator') {
      navigate('/flight-admin');
    }
  }, [navigate]);

  const fetchFlights = async () => {
    if (checkBox) {
      if (departure && destination && departureDate && returnDate) {
        const now = new Date();
        const date1 = new Date(departureDate);
        const date2 = new Date(returnDate);
        if (date1 > now && date2 > date1) {
          setError('');
          const response = await axios.get('http://localhost:6001/fetch-flights');
          setFlights(response.data);
        } else {
          setError('Please check the dates');
        }
      } else {
        setError('Please fill all the inputs');
      }
    } else {
      if (departure && destination && departureDate) {
        const now = new Date();
        const date1 = new Date(departureDate);
        if (date1 >= now) {
          setError('');
          const response = await axios.get('http://localhost:6001/fetch-flights');
          setFlights(response.data);
        } else {
          setError('Please check the dates');
        }
      } else {
        setError('Please fill all the inputs');
      }
    }
  };

  const handleTicketBooking = (id, origin, destination) => {
    if (userId) {
      if (origin === departure) {
        setTicketBookingDate(departureDate);
        navigate(`/book-flight/${id}`);
      } else if (destination === departure) {
        setTicketBookingDate(returnDate);
        navigate(`/book-flight/${id}`);
      }
    } else {
      navigate('/auth');
    }
  };

  return (
    <div className="landingPage">
      <div className="landingHero">
        <div className="landingHero-title">
          <h1 className="banner-h1">Embark on an Extraordinary Flight Booking Adventure!</h1>
          <p className="banner-p">
            Unleash your travel desires and book extraordinary Flight journeys that will transport you to unforgettable destinations.
          </p>
        </div>

        <div className="Flight-search-container input-container mb-4">
          <div className="form-check form-switch">
            <input className="form-check-input" type="checkbox" id="returnTrip" onChange={(e) => setCheckBox(e.target.checked)} />
            <label className="form-check-label" htmlFor="returnTrip">Return journey</label>
          </div>

          <div className="Flight-search-container-body">
            <div className="form-floating">
              <select className="form-select mb-3" value={departure} onChange={(e) => setDeparture(e.target.value)}>
                <option value="" disabled>Select</option>
                <option value="Chennai">Chennai</option>
                <option value="Banglore">Banglore</option>
                <option value="Hyderabad">Hyderabad</option>
                <option value="Mumbai">Mumbai</option>
                <option value="Indore">Indore</option>
                <option value="Delhi">Delhi</option>
                <option value="Pune">Pune</option>
                <option value="Trivendrum">Trivendrum</option>
                <option value="Bhopal">Bhopal</option>
                <option value="Kolkata">Kolkata</option>
                <option value="varanasi">varanasi</option>
                <option value="Jaipur">Jaipur</option>
              </select>
              <label>Departure City</label>
            </div>

            <div className="form-floating">
              <select className="form-select mb-3" value={destination} onChange={(e) => setDestination(e.target.value)}>
                <option value="" disabled>Select</option>
                <option value="Chennai">Chennai</option>
                <option value="Banglore">Banglore</option>
                <option value="Hyderabad">Hyderabad</option>
                <option value="Mumbai">Mumbai</option>
                <option value="Indore">Indore</option>
                <option value="Delhi">Delhi</option>
                <option value="Pune">Pune</option>
                <option value="Trivendrum">Trivendrum</option>
                <option value="Bhopal">Bhopal</option>
                <option value="Kolkata">Kolkata</option>
                <option value="varanasi">varanasi</option>
                <option value="Jaipur">Jaipur</option>
              </select>
              <label>Destination City</label>
            </div>

            <div className="form-floating mb-3">
              <input type="date" className="form-control" value={departureDate} onChange={(e) => setDepartureDate(e.target.value)} />
              <label>Journey Date</label>
            </div>

            {checkBox && (
              <div className="form-floating mb-3">
                <input type="date" className="form-control" value={returnDate} onChange={(e) => setReturnDate(e.target.value)} />
                <label>Return Date</label>
              </div>
            )}

            <button className="btn btn-primary" onClick={fetchFlights}>Search</button>
            {error && <p className="text-danger">{error}</p>}
          </div>
        </div>

        {Flights.length > 0 && (
          <div className="availableFlightsContainer">
            <h1>Available Flights</h1>
            <div className="Flights">
              {Flights
                .filter(flight =>
                  checkBox
                    ? (flight.origin === departure && flight.destination === destination) ||
                      (flight.origin === destination && flight.destination === departure)
                    : flight.origin === departure && flight.destination === destination
                )
                .map((flight) => (
                  <div className="Flight" key={flight._id}>
                    <div><b>{flight.flightName}</b></div>
                    <div><b>Flight Number:</b> {flight.flightId}</div>
                    <div><b>From:</b> {flight.origin}</div>
                    <div><b>To:</b> {flight.destination}</div>
                    <div><b>Departure:</b> {flight.departureTime}</div>
                    <div><b>Arrival:</b> {flight.arrivalTime}</div>
                    <div><b>Price:</b> â‚¹{flight.basePrice}</div>
                    <div><b>Seats:</b> {flight.totalSeats}</div>
                    <button className="btn btn-success mt-2" onClick={() => handleTicketBooking(flight._id, flight.origin, flight.destination)}>Book Now</button>
                  </div>
                ))}
            </div>
          </div>
        )}
      </div>

      <section id="about" className="section-about p-4">
        <div className="container">
          <h2 className="section-title">About Us</h2>
          <p className="section-description">Welcome to our Flight ticket booking app, where we are dedicated to providing an exceptional travel experience from start to finish...</p>
          <span><h5>2023 SB FlightConnect - &copy; All rights reserved</h5></span>
        </div>
      </section>
    </div>
  );
};

export default LandingPage;
