import React, { useEffect, useState } from 'react';
import Navbar from '../components/Navbar';
import '../styles/allUsers.css';
import axios from 'axios';

const AllUsers = () => {
  const [users, setUsers] = useState([]);

  useEffect(() => {
    fetchUsers();
  }, []);

  const fetchUsers = async () => {
    const res = await axios.get('http://localhost:6001/fetch-users');
    setUsers(res.data);
  };

  return (
    <>
      <Navbar />
      <div className="all-users-page">
        <h2 className="title">Users & Operators</h2>
        <div className="users-operators-container">
          {/* All Users */}
          <div className="user-section">
            <h3>All Users</h3>
            <div className="all-users">
              {users.filter(user => user.usertype === 'customer').map((user) => (
                <div className="user" key={user._id}>
                  <p><b>UserId:</b> {user._id}</p>
                  <p><b>Username:</b> {user.username}</p>
                  <p><b>Email:</b> {user.email}</p>
                </div>
              ))}
            </div>
          </div>

          {/* All Flight Operators */}
          <div className="user-section">
            <h3>Flight Operators</h3>
            <div className="all-users">
              {users.filter(user => user.usertype === 'flight-operator').map((user) => (
                <div className="user" key={user._id}>
                  <p><b>OperatorId:</b> {user._id}</p>
                  <p><b>Flight Name:</b> {user.username}</p>
                  <p><b>Email:</b> {user.email}</p>
                </div>
              ))}
            </div>
          </div>
        </div>
      </div>
    </>
  );
};

export default AllUsers;
