import React, { useEffect, useState } from 'react';
import '../styles/Bookings.css';
import axios from 'axios';

const Bookings = () => {
  const [bookings, setBookings] = useState([]);
  const userId = localStorage.getItem('userId');

  useEffect(() => {
    fetchBookings();
  }, []);

  const fetchBookings = async () => {
    const res = await axios.get('http://localhost:6001/fetch-bookings');
    setBookings(res.data.reverse());
  };

  const cancelTicket = async (id) => {
    await axios.put(`http://localhost:6001/cancel-ticket/${id}`);
    alert('Ticket cancelled!');
    fetchBookings();
  };

  return (
    <div className="bookings-container">
      <h1 className="bookings-heading">Your Bookings</h1>

      <div className="bookings-grid">
        {bookings.filter(b => b.user === userId).map((booking) => (
          <div className="booking-card" key={booking._id}>
            <p className="booking-text"><span className="booking-label">Booking ID:</span> {booking._id}</p>

            <div className="booking-section">
              <p className="booking-text"><span className="booking-label">Mobile:</span> {booking.mobile}</p>
              <p className="booking-text"><span className="booking-label">Email:</span> {booking.email}</p>
            </div>

            <div className="booking-section">
              <p className="booking-text"><span className="booking-label">Flight ID:</span> {booking.flightId}</p>
              <p className="booking-text"><span className="booking-label">Flight Name:</span> {booking.flightName}</p>
            </div>

            <div className="booking-section">
              <p className="booking-text"><span className="booking-label">Onboarding:</span> {booking.departure}</p>
              <p className="booking-text"><span className="booking-label">Destination:</span> {booking.destination}</p>
            </div>

            <div className="booking-section booking-passengers">
              <div>
                <p className="booking-label">Passengers:</p>
                <ol>
                  {booking.passengers.map((p, i) => (
                    <li key={i}>
                      <span><strong>Name:</strong> {p.name}, <strong>Age:</strong> {p.age}</span>
                    </li>
                  ))}
                </ol>
              </div>
              {booking.bookingStatus === 'confirmed' && (
                <p className="booking-text"><span className="booking-label">Seats:</span> {booking.seats}</p>
              )}
            </div>

            <div className="booking-section">
              <p className="booking-text"><span className="booking-label">Booking Date:</span> {booking.bookingDate.slice(0, 10)}</p>
              <p className="booking-text"><span className="booking-label">Journey Date:</span> {booking.journeyDate.slice(0, 10)}</p>
            </div>

            <div className="booking-section">
              <p className="booking-text"><span className="booking-label">Time:</span> {booking.journeyTime}</p>
              <p className="booking-text"><span className="booking-label">Total Price:</span> ₹{booking.totalPrice}</p>
            </div>

            <p className={`booking-status ${booking.bookingStatus}`}>
              <span className="booking-label">Status:</span> {booking.bookingStatus}
            </p>

            {booking.bookingStatus === 'confirmed' && (
              <div className="cancel-button">
                <button className="btn btn-danger" onClick={() => cancelTicket(booking._id)}>
                  Cancel Ticket
                </button>
              </div>
            )}
          </div>
        ))}
      </div>
    </div>
  );
};

export default Bookings;
